mapper.select:
insert into file (
    seq
    ,master_seq
    ,file_name
    ,file_path
    ,file_type
    ,file_size
    ,file_ext
) values (
    nextval('seq')
    ,#{fileMasterSeq}::numeric
    ,#{fileName}
    ,#{filePath}
    ,#{fileType}
    ,#{fileSize}
    ,#{fileExt}
)
returning currval('seq')
